// Layout.jsx
import React from 'react';
import { Outlet } from 'react-router-dom';
import Header from './Header';
import CartTab from './CartTab';
import { useSelector } from 'react-redux';
import Home from '../pages/Home';
import { products } from '../data/products';

const Layout = () => {
  const statusTabCart = useSelector((store) => store.cart.statusTab);

  return (
    <div className='bg-zinc-200'>
      <main
        className={`w-[1200px] max-w-full m-auto p-5 transform transition-transform duration-500 ${
          statusTabCart === false ? '' : '-translate-x-56'
        }`}
      >
        <Header onSearch={(term) => console.log('Search Term:', term)} /> {/* Pass handleSearch from Home */}
        <Outlet />
        <Home products={products} /> {/* Render Home with products */}
      </main>
      <CartTab />
    </div>
  );
};

export default Layout;
