import React, { useState, useEffect } from 'react';
import ProductCart from '../components/ProductCart';
import axios from 'axios';

const categories = ['All', 'Laptops', 'Accessories'];

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState(''); // State for search term

  useEffect(() => {
    // Fetch products from backend
    axios.get('/api/products')
      .then(response => {
        setProducts(response.data);
        setFilteredProducts(response.data);
      })
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  useEffect(() => {
    // Filter products based on search term and selected category
    handleSearch(searchTerm);
  }, [products, selectedCategory]);

  // Handle search filtering
  const handleSearch = (searchTerm) => {
    const filtered = products.filter(
      (product) =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (selectedCategory === 'All' || product.category === selectedCategory)
    );
    setFilteredProducts(filtered);
  };

  // Handle category filtering
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    handleSearch(searchTerm); // Reapply search filtering after changing category
  };

  return (
    <div>
      <h1 className="text-3xl my-5">List of Products</h1>

      {/* Search Bar */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            handleSearch(e.target.value); // Apply search filtering on change
          }}
          className="p-2 border rounded"
        />
      </div>

      {/* Product Category Menu */}
      <div className="mb-4">
        {categories.map((category) => (
          <button
            key={category}
            className={`mr-2 ${
              selectedCategory === category ? 'bg-gray-500 text-white' : 'bg-gray-300'
            } p-2 rounded`}
            onClick={() => handleCategoryChange(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="grid lg:grid-cols-4 md:grid-cols-3 sm:grid-cols-2 gap-5">
        {Array.isArray(filteredProducts) &&
          filteredProducts.map((product) => (
            <ProductCart key={product.id} data={product} />
          ))}
      </div>
    </div>
  );
};

export default Home;
