import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { products } from '../data/products';
import { useDispatch } from 'react-redux';
import { addToCart } from '../store/cart';

const Details = () => {
  const { slug } = useParams();
  const [detail, setDetails] = useState([]); 
  const [quantity, setQuantity] = useState(1);
  const dispatch = useDispatch(); 
  const navigate = useNavigate(); 

  useEffect(() => {
    const findDetail = products.filter((product) => product.slug === slug);
    if (findDetail.length > 0) {
      setDetails(findDetail[0]);
    } else {
      navigate('/');
    }
  }, [slug,navigate]);

  const handleMinusQuantity = () =>{
    setQuantity(quantity -1 < 1 ? 1 : quantity - 1);
    
  }

  const handlePlusQuantity = () =>{
    setQuantity(quantity + 1);
  }

  const handleAddToCart = () => {
    dispatch(addToCart({
      productId: detail.id,
      quantity: quantity
  }));
  }

  return (
    <div>
      <h2 className="text-3xl text-center">PRODUCT DETAIL</h2>
      <div className="grid grid-cols-2 gap-5 mt-5">
        <div>
          {detail.image && (
            <img src={detail.image} alt={detail.name} className="w-full" />
          )}
        </div>
        <div className="flex flex-col gap-5 items-start">
          <h1 className="text-4xl uppercase font-bold">{detail.name}</h1>
          <p className="font-bold text-3xl">${detail.price}</p>
          <div className="flex gap-5">
            <div className="flex gap-2 justify-center items-center">
              <button className="bg-gray-100 h-full w-10 font-bold text-xl rounded-xl flex justify-center" onClick={handleMinusQuantity}>
                -
              </button>
              <span className="bg-gray-200 h-full w-10 font-bold text-xl rounded-xl flex justify-center">
                {quantity}
              </span>
              <button className="bg-gray-100 h-full w-10 font-bold text-xl rounded-xl flex justify-center" onClick={handlePlusQuantity}>
                +
              </button>
            </div>
            <button className='bg-slate-900 text-white px-7 rounded-xl py-3 shadow-2xl' onClick={handleAddToCart}>Add To Cart</button>
          </div>
          <p className="text-start">{detail.description}</p>
        </div>
      </div>
    </div>
  );
};

export default Details;
