import React from 'react'
import { useParams } from 'react-router-dom'
import { products } from '../data/products'
import { useEffect,useState } from 'react'

const Details = () => {
  const {slug} = useParams();
  const {detail, setDetails} = useState([]);

  useEffect(() => {
    const findDetail = products.filter(product => product.slug === slug);
    if(findDetail.length > 0) {
      setDetails(findDetail[0]);
    }else{
      window.location.href="/";
    }
  }, [slug]);

  return (
    <div>
      <h2 className="text-3xl text-center">PRODUCT DETAIL</h2>
      <div className="grid grid-cols-2 gap-5 mt-5">
        <div>
          <img src={detail.image} alt="" className='w-full'/>
        </div>
        <div className="flex flex-col gap-5">
          <h1 className="text-4xl uppercase font-bold">{detail.name}</h1>
          <p className="font-bold text-3xl">
             ${detail.price}
          </p>
          <div className="flex gap-5">
            <div className="flex gap-2 justify-center items-center">
              <button className="bg-gary-100 h-full w-10 font-bold text-xl rounded-xl flex justify-center ">-</button>
              <span className="bg-gary-200 h-full w-10 font-bold text-xl rounded-xl flex justify-center ">1</span>
              <button className="bg-gary-100 h-full w-10 font-bold text-xl rounded-xl flex justify-center ">+</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Details
